mapper.select:
select
id, mac_id, start_mac_address, end_mac_address, application_id, amount, createDate, creator, updateDate,
updator, status
from bill.delivery_record
where status <> 0

        and id = #{id}


        and mac_id = #{macId}


        and start_mac_address = #{startMacAddress}


        and end_mac_address = #{endMacAddress}


        and application_id = #{applicationId}


        and amount = #{amount}


        and createDate BETWEEN #{createdate} and DATE_ADD(DATE_FORMAT(#{createdate},'%Y-%m-%d'),INTERVAL 1 DAY)


        and creator = #{creator}


        and updateDate = #{updatedate}


        and updator = #{updator}


        and status = #{status}

order by updateDate desc

select id, mac_id, start_mac_address, end_mac_address, application_id, amount, createDate, creator, updateDate, updator, status from bill.delivery_record where 1=1
and start_mac_address = #{startMacAddress}
and date(createDate)=#{createdate}
order by updateDate desc
